import { ErrorMessage, Field, Form, Formik } from "formik";
import * as Yup from 'yup';
import React, { useState } from "react";


const Calculator = () => {

    const [weight, setWeight] = useState(0)
    const [height, setHeight] = useState(0)
    const [bmi, setBmi] = useState('')
    const [message, setMessage] = useState('')

    let calcBmi = (event) => {
        event.preventDefault()

        let bmi = (event.target.weight / (event.target.height * event.target.height) * 703)
        setBmi(bmi.toFixed(1))

        if (bmi < 25) {
            setMessage('You are underweight')
        } else if (bmi >= 25 && bmi < 30) {
            setMessage('You are a healthy weight')
        } else {
            setMessage('You are overweight')
        }

    }


    return (
        <div>
            <h2>BMI calculator</h2>

            <Formik
                initialValues={{ weight: '', height: '' }}

                validationSchema={Yup.object({
                    weight: Yup.string()
                        .min(2, 'Minimum weight value is 2 characters')
                        .required('Required'),
                    height: Yup.string()
                        .min(20, 'Minimum height value is 2 characters')
                        .required('Required')
                })}

                onSubmit={(values, { setSubmitting }) => {
                    debugger;
                    calcBmi(values);
                    setSubmitting(false);
                }}
            >
                <Form>
                    <label htmlFor="weight">Weight(ibs)</label>
                    <Field name="weight" type="text" placeholder="Enter Weight" />
                    <ErrorMessage name="weight" />

                    <label htmlFor="height">Height (in)</label>
                    <Field name="height" type="text" placeholder="Enter Height" />
                    <ErrorMessage name="height" />

                    <button type="submit">Submit</button>

                    <button type="reset">Reload</button>
                </Form>
            </Formik>

            <div className='center'>
                <h3>Your BMI is: {bmi}</h3>
                <p>{message}</p>
            </div>


        </div>
    )
}

export default Calculator;
